import React from 'react';
import './ShipmentTable.scss';
class ShipmentTable extends React.Component {
    constructor(props) {
        super(props);
        this.state = {
            ship_data: this.props.ship_data
        }
    }

    componentWillReceiveProps() {
        this.setState({ ship_data: this.props.ship_data });
    }

    componentDidMount() {

    }

    componentDidUpdate() {
        if (this.state.ship_data !== this.props.ship_data) {
            this.setState({ ship_data: this.props.ship_data });
        }
    }

    render() {
        if (!this.state.ship_data.length)
            return null;
        else {
            return (
                <>
                    <table>
                        <tr>
                            <th>ID</th>
                            <th>Name</th>
                            <th>Mode</th>
                            <th>Type</th>
                            <th>Origin</th>
                            <th>Destination</th>
                        </tr>
                        {this.state.ship_data.map((d, index) => {
                            if (d !== undefined && d !== null) {
                                return (
                                    <tr key={index}>
                                        <td>{d.id}</td>
                                        <td>{d.name}</td>
                                        <td>{d.mode}</td>
                                        <td>{d.type}</td>
                                        <td>{d.origin}</td>
                                        <td>{d.destination}</td>
                                    </tr>
                                )
                            }
                        })}
                    </table>
                </>
            )
        }
    }
}

export default ShipmentTable;
