import React from 'react';
import ShipmentTable from '../Shipment Table/ShipmentTable';
import * as _ from 'lodash';

class Shipment extends React.Component {

    allShipmentdata = [];
    filteredData = [];
    pageNo;
    pageSize;
    constructor(props) {
        super(props);
        this.state = {
            shipment_data: []
        }
    }

    componentDidMount() {
        this.getShipmentData();
    }

    // shouldComponentUpdate(){

    // }

    componentDidUpdate() {

    }

    getShipmentData() {
        fetch('http://localhost:3000/shipments')
            .then(res => res.json())
            .then(
                (result) => {
                    this.allShipmentdata = result;
                    this.filteredData = result;
                    this.setState({ shipment_data: result });
                    console.log(this.state.shipment_data);
                }, (error) => {
                    console.log(error);
                }
            )
    }

    manipulatePage(type) {
        if (type === 'size') {
            if (this.refs.pageSize.value) {
                console.log(this.refs.pageSize.value)
            } else
                alert('Please Enter Page Size...');

        } else if (type === 'sort') {
            if (this.refs.sort_select.value) {
                console.log(this.refs.sort_select.value);
                let sortBy = '';
                let sortedData = [];
                if(this.refs.sort_select.value === 'ID'){
                  sortedData = _.sortBy(this.allShipmentdata , 'id');
                  console.log();
                  this.setState({ shipment_data : sortedData });
                } else if(this.refs.sort_select.value === 'Name'){
                    sortedData = _.sortBy(this.allShipmentdata , 'name');
                    console.log();
                    this.setState({ shipment_data : sortedData });
                    this.shouldComponentUpdate(true);
                } else if(this.refs.sort_select.value === 'Mode'){

                } else if(this.refs.sort_select.value === 'Type'){

                } else if(this.refs.sort_select.value === 'Origin'){

                } else if(this.refs.sort_select.value === 'Destination'){

                }

            }
        } else if (type === 'search') {
            if (this.refs.search_data.value) {
                console.log(this.refs.search_data.value)
            }
        } else if (type === 'reset') {
            console.log('reseting...');
            this.setState({ shipment_data: this.allShipmentdata });
        } else {
            if (this.refs.pageNo.value) {
                console.log(this.refs.pageNo.value);
            }
            else
                alert('Please enter Page No....');
        }
    }

    render() {
        if (!this.state.shipment_data.length)
            return null;
        else
            return (
                <div>Shipment Data...!!
                <div>
                        <div>
                            <input type="textbox" placeholder="Enter Page No" ref="pageNo" />
                            <button type="submit" value="submit" onClick={e => this.manipulatePage('no')}>Go to Page</button>
                            <input type="textbox" placeholder="Enter Page Size" ref="pageSize" />
                            <button type="submit" value="submit" onClick={e => this.manipulatePage('size')}>Go to Page</button>
                        </div>
                        <div>
                            <input type="textbox" name="search" placeholder="Enter data to search" ref="search_data"></input>
                            <button type="submit" value="submit" onClick={e => this.manipulatePage('search')} >Search</button>
                            <button type="submit" value="submit" onClick={e => this.manipulatePage('reset')}>Reset Search</button>
                            <select name="sort_select" id="sort_select" ref="sort_select">
                                <option value="ID">ID</option>
                                <option value="Name">Name</option>
                                <option value="Mode">Mode</option>
                                <option value="Type">Type</option>
                                <option value="Origin">Origin</option>
                                <option value="Destination">Destination</option>
                            </select>
                            <button type="submit" value="submit" onClick={e => this.manipulatePage('sort')} >Sort By</button>
                        </div>
                    </div>
                    <div>
                        <ShipmentTable ship_data={this.state.shipment_data}></ShipmentTable>
                    </div>
                </div>
            )
    }
}

export default Shipment;
